import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import * as actions from './redux/actions';
import { Upload, message, Button } from 'antd';
import { InboxOutlined } from '@ant-design/icons';
import TemplateChart from './TemplateChart';
import '../../styles/SpineContent.less';

const { Dragger } = Upload;

const props = {
  onChange(info) {
    const { status } = info.file;
    if (status !== 'uploading') {
      console.log(info.file, info.fileList);
    }
    if(info.file.size/1024/1024>50){
      message.error(`${info.file.name} has exceeded 50 MB.`)
      return;
    }
    if (status === 'done') {
      message.success(`${info.file.name} file uploaded successfully.`);
    } else if (status === 'error') {
      message.error(`${info.file.name} file upload failed.`);
    }
  },
  onDrop(e) {
    console.log('Dropped files', e.dataTransfer.files);
  },
};

export class SpineContent extends Component {
  static propTypes = {
    home: PropTypes.object.isRequired,
    actions: PropTypes.object.isRequired,
  };
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      fileList: [], //文件列表，用于控制upload组件
    };
  }

  handleFileChange = ({ file, fileList }) => {
    //处理文件change，保证用户选择的文件只有一个
    this.setState({
      fileList: fileList.length ? [fileList[fileList.length - 1]] : [],
    });
  };

  handleUpload = () => {
    if (!this.state.fileList.length) {
      message.warning('请选择要上传的文件');
    }

    const formData = new FormData();
    formData.append('file', this.state.fileList[0].originFileObj);

    this.props.actions.uploadFile(formData);
  };

  render() {
    return (
      <div>
        <Dragger
          name="file"
          multiple={false}
          action=""
          beforeUpload={(f, fList) => fList.length === 1?true:false}
          onChange={this.handleFileChange}
          accept=".csv, .xls, .xlsx"
        >
          <p className="ant-upload-drag-icon">
            <InboxOutlined />
          </p>
          <p className="ant-upload-text">Click or drag file to this area to upload</p>
          <p className="ant-upload-hint">
            Supported file format: csv, excel. File size cannot exceed 50MB.
          </p>
        </Dragger>
        <Button
          type="primary"
          onClick={this.handleUpload}
          disabled={this.state.fileList.length === 0}
          loading={this.props.home.isUploading}
          size="large"
          style={{ marginTop: 16, marginLeft: "47%"
 }}
        >
          {this.props.home.isUploading ? 'Uploading' : 'Start Upload'}
        </Button>
        <TemplateChart  data={this.props.home.parsedData}/>
      </div>
    );
  }

  // render() {
  //   const fileList = this.state.fileList;
  //   const uploading = this.state.uploading;
  //   return (
  //     <div>
  //       <Upload
  //         fileList={this.state.fileList}
  //         beforeUpload={(f, fList) => false}
  //         onChange={this.handleFileChange}
  //       >
  //         <Button>
  //           <CloudUploadOutlined /> 选择文件
  //         </Button>
  //       </Upload>
  //       <Button
  //         type="primary"
  //         onClick={this.handleUpload}
  //         disabled={fileList.length === 0}
  //         loading={uploading}
  //         style={{ marginTop: 16 }}
  //       >
  //         {uploading ? 'Uploading' : 'Start Upload'}
  //       </Button>
  //     </div>
  //   );
  // }
}

/* istanbul ignore next */
function mapStateToProps(state) {
  return {
    home: state.home,
  };
}

/* istanbul ignore next */
function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...actions }, dispatch),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(SpineContent);
